import Trip from "../api/Trip";
import Faker from "faker/locale/es";
import { mapState } from "vuex";

export default {
  computed: {
    // ...mapState({
    //   admin: state => state.admin
    // })
    admin: true,
    currentUser() {
      return this.$store.state.user;
    }
  },
  data: () => ({
    tripInEditingProcess: null,
    editing: false,
    valid: true,
    city: "",
    cityRules: [v => !!v || "City is required"],
    country: "",
    countryRules: [v => !!v || "Country is required"],
    departure: "",
    //
    departureRules: [
      v =>
        /((0?[1-9]|1[012])[- /.](0?[1-9]|[12][0-9]|3[01])[- /.](19|20)?[0-9]{2})/.test(
          v
        ) || "Date must be valid",
      v => !!v || "Departure is required"
    ],
    arrival: "",
    //
    arrivalRules: [
      v =>
        /((0?[1-9]|1[012])[- /.](0?[1-9]|[12][0-9]|3[01])[- /.](19|20)?[0-9]{2})/.test(
          v
        ) || "Date must be valid",
      v => !!v || "Arrival is required"
    ],
    description: "",
    descriptionRules: [v => !!v || "Description is required"],
    price: "",
    priceRules: [
      v => !!v || "Price is required",
      v => /[0-9]/.test(v) || "Price must be valid"
    ]
  }),
  methods: {
    validate() {
      if (this.$refs.form.validate()) {
        this.snackbar = true;
        this.register();
      }
    },
    register() {
      //  let ran = Math.ceil(Math.random() * 1000000);
      const trip = {
        city: this.city,
        country: this.country,
        photo: Faker.image.city(),
        departure: this.departure,
        arrival: this.arrival,
        description: this.description,
        price: this.price
      };
      console.log(trip);
      Meteor.call("trip.add", trip);
    },
    reset() {
      this.$refs.form.reset();
    },
    formatDate(date) {
      var d = new Date(date),
        month = "" + (d.getMonth() + 1),
        day = "" + d.getDate(),
        year = d.getFullYear();

      if (month.length < 2) month = "0" + month;
      if (day.length < 2) day = "0" + day;

      return [year, month, day].join("-");
    },
    editProcess(item) {
      this.tripInEditingProcess = item;
      this.editing = true;
      this.city = item.city;
      this.country = item.country;
      this.departure = item.departure;
      this.arrival = item.arrival;
      this.description = item.description;
      this.price = item.price;
    },
    editTrip() {
      let trip = {
        _id: this.tripInEditingProcess._id,
        city: this.city,
        country: this.country,
        photo: this.tripInEditingProcess.photo,
        departure: this.departure,
        arrival: this.arrival,
        description: this.description,
        price: this.price
      };
      this.editing = false;
      Meteor.call("trip.edit", trip);
      this.reset();
    },
    isAdmin(){
      return currentUser.isAdmin
    }
  },
  meteor: {
    $subscribe: {
      trip: []
    },
    trip() {
      return Trip.find({}, { limit: 10, sort: { date: -1 } });
    }
  }
};
